    garafu.nav.init = function () {
        $('#btnNavbarToggleSearch').on('click', garafu.nav.btnNavbarToggleSearch_onclick);
        $('#frmNavbarSearch input').on('click', false);
        $('#btnNavbarToggleMenu').on('click', garafu.nav.btnNavbarToggleMenu_onclick);
    };

    garafu.nav.btnNavbarToggleSearch_onclick = function (event) {
        $('#frmNavbarSearch').toggleClass('in');
        $('#frmNavbarSearch input').val('').focus();

        window.setTimeout(function () {
            var callback = function (event) {
                $('#frmNavbarSearch').toggleClass('in');
                $(document.body).off('click', callback);
            };

            $(document.body).on('click', callback);
        }, 100);
    };

    garafu.nav.btnNavbarToggleMenu_onclick = function (event) {
        $('#navbar-menu').toggleClass('in');
    };

    /**
     * Index に関する機能を初期化します。
     */
    garafu.idx.init = function () {
        var i, item;

        if (BLOG_PAGE_TYPE != 'index') {
            return;
        }

        for (i = SUMMARY_TARGET_LIST.length; i--;) {
            item = SUMMARY_TARGET_LIST[i];
            garafu.idx.createSummaryAndThumbnail(item.id, item.url, item.title);
        }
    };

    garafu.idx.createSummaryAndThumbnail = function (id, url, title) {
        var NO_IMAGE_SRC = '//4.bp.blogspot.com/-9mCCaXTpn9I/VN4hDMsFXlI/AAAAAAAAC6U/i84N2Ng37Ik/s1600/noimage.png';
        var $body, text, summary, $anchor, $container;

        $body = $(document.getElementById(id));

        // 画像取得
        var src = $body.find('img:first').attr('src') || NO_IMAGE_SRC;

        // サムネイル作成
        var $thumbnail = $(document.createElement('span'));
        $thumbnail.css('background-image', 'url(' + src + ')');
        $thumbnail.addClass('index-post-thumbnail');

        // 本文取得
        text = $body.text() || '';
        text = text.replace(/<!--[\s\S]*-->/g, '');
        text = jQuery.trim(text);

        // サマリ作成
        if (text.length > SUMMARY_TEXT_MAX_COUNT) {
            summary = text.substr(0, SUMMARY_TEXT_MAX_COUNT - 4);
            summary += ' ...';
        } else {
            summary = text;
        }

        var $summary = $(document.createElement('div'));
        $summary.addClass('index-post-summary');
        $summary.text(summary);

        // サマリ要素表示
        $body.empty();
        $body.append($thumbnail);
        $body.append($summary);
        $body.show();

        // アンカータグの生成
        $anchor = $(document.createElement('a'));
        $anchor.attr('href', url);
        $anchor.attr('title', title);

        // DOMの再構築
        $container = $body.parents('.index-post-item');
        $container.children('a').remove();
        $anchor.append($container.children());
        $container.append($anchor);
    };

