    garafu.lib.init = function () {
        switch (BLOG_PAGE_TYPE) {
            case 'index':
                break;
            case 'archive':
                break;
            case 'item':
                this.initSyntaxHighlighter();
                break;
            case 'static_page':
                this.initSyntaxHighlighter();
                break;
            default:
        }
    };

    garafu.lib.initSyntaxHighlighter = function () {
        // コマンド用のブラシを追加
        SyntaxHighlighter.brushes.Cmd = function () { this.regexList = [{ regex: /(^::|rem).*$/gmi, css: 'comments' }, { regex: SyntaxHighlighter.regexLib.doubleQuotedString, css: 'string' }, { regex: SyntaxHighlighter.regexLib.singleQuotedString, css: 'string' }, { regex: /`(?:\.|(\\\`)|[^\``\n])*`/g, css: 'string' }, { regex: /echo.*$/gmi, css: 'string' }, { regex: /^:.+$/gmi, css: 'color3' }, { regex: /(%|!)\w+\1/gmi, css: 'variable' }, { regex: /%\*|%%?~?[fdpnxsatz]*[0-9a-z]\b/gmi, css: 'variable' }, { regex: RegExp(this.getKeywords('ASSOC AT ATTRIB BREAK CACLS CD CHCP CHDIR CHKDSK CHKNTFS CLS CMD COLOR COMP COMPACT CONVERT COPY DATE DEL DIR DISKCOMP DISKCOPY DOSKEY ECHO ERASE EXIT FC FIND FINDSTR FORMAT FTYPE GRAFTABL HELP LABEL MD MKDIR MODE MORE MOVE PATH PAUSE POPD PRINT PROMPT PUSHD RD RECOVER REN RENAME REPLACE RMDIR SHIFT SORT START SUBST TIME TITLE TREE TYPE VER VERIFY VOL XCOPY'), 'gmi'), css: 'keyword' }, { regex: RegExp(this.getKeywords('CON DEFINED DO ENABLEDELAYEDEXPANSION ENABLEEXTENSIONS ENDLOCAL FOR GOTO CALL IF IN ELSE NOT NUL REM SET SETLOCAL'), 'gmi'), css: 'keyword' }] }; SyntaxHighlighter.brushes.Cmd.prototype = new SyntaxHighlighter.Highlighter; SyntaxHighlighter.brushes.Cmd.aliases = ['bat', 'cmd', 'batch'];

