    garafu.lib.init = function () {
        switch (BLOG_PAGE_TYPE) {
            case 'index':
                this.initButtonTop();
                this.initPostList();
                break;
            case 'archive':
                this.initButtonTop();
                this.initPostList();
                break;
            case 'item':
                this.initButtonTop();
                this.initSyntaxHighlighter();
                break;
            case 'static_page':
                this.initButtonTop();
                this.initSyntaxHighlighter();
                break;
            default:
        }
    };

    garafu.lib.initButtonTop = function () {
        var $navbar = $(document.getElementById('navbar'));
        var $btntop = $(document.getElementById('btn-top'));
        var $footer = $(document.getElementById('footer'));
        var defaultNavbarTop = $navbar.offset().top;
        var isDisplayed = false;

        var btntop_onclick = function (event) {
            $('html,body').animate({ scrollTop: 0 });
        };

        var window_onscroll = function (event) {
            var top = $(window).scrollTop();
            var navbarTop = 0; //$navbar.offset().top;
            var navbarHeight = $navbar.height();
            var footerTop = $footer.offset().top;

            if (top >= navbarTop + navbarHeight) {
                if (isDisplayed === false) {
                    $btntop.fadeIn();
                    isDisplayed = true;
                }
            } else {
                $btntop.fadeOut();
                isDisplayed = false;
            }
        };

        $(window).on('scroll', window_onscroll);
        $(document.getElementById('btn-top')).on('click', btntop_onclick);
    };

    garafu.lib.initPostList = function () {
        // 記事サマリの作成
        for (i = SUMMARY_TARGET_LIST.length; i--;) {
            item = SUMMARY_TARGET_LIST[i];
            this.createSummaryAndThumbnail(item.id, item.url, item.title);
        }
    };

    garafu.lib.createSummaryAndThumbnail = function (id, url, title) {
        var NO_IMAGE_SRC = '//4.bp.blogspot.com/-9mCCaXTpn9I/VN4hDMsFXlI/AAAAAAAAC6U/i84N2Ng37Ik/s1600/noimage.png';
        var $body, text, summary, $anchor, $container;

        $body = $(document.getElementById(id));

        // 画像取得
        var src = $body.find('img:first').attr('src') || NO_IMAGE_SRC;

        // サムネイル作成
        var $thumbnail = $(document.createElement('span'));
        $thumbnail.css('background-image', 'url(' + src + ')');
        $thumbnail.addClass('index-post-thumbnail');

        // 本文取得
        text = $body.text() || '';
        text = text.replace(/<!--[\s\S]*-->/g, '');
        text = jQuery.trim(text);

        // サマリ作成
        if (text.length > SUMMARY_TEXT_MAX_COUNT) {
            summary = text.substr(0, SUMMARY_TEXT_MAX_COUNT - 4);
            summary += ' ...';
        } else {
            summary = text;
        }

        var $summary = $(document.createElement('div'));
        $summary.addClass('index-post-summary');
        $summary.text(summary);

        // サマリ要素表示
        $body.empty();
        $body.append($thumbnail);
        $body.append($summary);
        $body.show();

        // アンカータグの生成
        $anchor = $(document.createElement('a'));
        $anchor.attr('href', url);
        $anchor.attr('title', title);

        // DOMの再構築
        $container = $body.parents('.index-post-item');
        $container.children('a').remove();
        $anchor.append($container.children());
        $container.append($anchor);
    };

