            'sql                    //cdnjs.cloudflare.com/ajax/libs/SyntaxHighlighter/3.0.83/scripts/shBrushSql.js',
            'vb vbnet               //cdnjs.cloudflare.com/ajax/libs/SyntaxHighlighter/3.0.83/scripts/shBrushVb.js',
            'xml xhtml xslt html    //cdnjs.cloudflare.com/ajax/libs/SyntaxHighlighter/3.0.83/scripts/shBrushXml.js'
        );

        // ブラシの自動読み込み
        SyntaxHighlighter.all();
    };

    // ドキュメント読み込み後のエントリーポイント
    $(document).ready(function (event) {
        garafu.nav.init();
        garafu.idx.init();
        garafu.itm.init();
        garafu.lib.init();
    });
